import React from "react";


class Entries extends React.Component {
  constructor(props) {
    super(props)
    this.state={
        initialDebt: 200000,
        initialInterest:1,
        monthlyInterest:0,
        debtLeft:0,
        minimumDue:0,
        paymentsLeft:0,
    }
}

handleChange =  (e) => {
    this.setState({[`${e.target.name}`]:e.target.value})
}

getData = () => {

    const{initialDebt, initialInterest} = this.state
    const monthlyInt = (initialInterest / 100) / 12;
    const monthlyIntPay = monthlyInt * initialDebt
    const minTotalPay = monthlyIntPay + (initialDebt * 0.01)
    const totalPayoff = minTotalPay + initialDebt;

    this.setState({
        monthlyInterest: monthlyInt * initialDebt, 
        debtLeft: initialDebt, 
        minimumDue: minTotalPay, 
        paymentsLeft: initialDebt / minTotalPay
    });

    this.props.getData(minTotalPay, initialDebt, totalPayoff);

}
calcDebt= () => {
if (this.state.debtLeft > 100) {
    const{ initialInterest, debtLeft} = this.state
    const {payment} = this.props;

    const monthlyIntPay = ((initialInterest / 100) / 12) * debtLeft
    const newMinPay = monthlyIntPay + (debtLeft * 0.01)
    const principlePaid = payment - monthlyIntPay
    let newDebt= debtLeft - principlePaid
    let totalPayoff = newMinPay + newDebt;

    this.setState({
        monthlyInterest: ((initialInterest / 100) / 12) * newDebt, 
        debtLeft: newDebt, 
        minimumDue: newMinPay, 
        paymentsLeft: newDebt / principlePaid
    });

    this.props.getData(newMinPay, newDebt, totalPayoff);
    }
else {
    const{debtLeft} = this.state
    const {payment} = this.props;
    const newDebt= debtLeft - (payment)
    const newMinPay = (debtLeft * 0.01) + newDebt
    const totalPayoff = (debtLeft * 0.01) + newDebt

    this.setState({
        debtLeft: newDebt, 
        minimumDue: newMinPay, 
        paymentsLeft: newDebt / newMinPay
    });

    this.props.getData(newMinPay, newDebt, totalPayoff);
    }
} 
reset=()=>{
    this.setState({ 
        initialDebt: 200000, 
        initialInterest:1, 
        monthlyInterest:0, 
        debtLeft:0, 
        minimumDue:0, 
        paymentsLeft:0,})
}

render() {

    const {initialDebt, initialInterest, debtLeft, minimumDue, paymentsLeft } = this.state;

    const inputData = [
        {header:'Your new loan estimate:', label: `$${initialDebt}`, name:'initialDebt', type:"range",min:"1000", 
        max:"200000", value: initialDebt ,step:'500', onChange: this.handleChange , className:"slider", id:"debtRange"},
        {header:'APR (Annual Percentage Rate) for Loan 1', label: `${initialInterest}%`, name:'initialInterest', type:"range",min:"1", 
        max:"20", value: initialInterest ,step:'0.1', onChange: this.handleChange , className:"slider", id:"interestRange"}
    ]
    return (

    <div>    
        <div className='container inputs'>
            {inputData.map((item) => 
            <div>
                <h2>{item.header}</h2>
                <div className='total'>{item.label}</div>
                <input 
                    name={item.name}
                    type={item.type} 
                    min={item.min} 
                    max={item.max} 
                    value={item.value} 
                    step={item.step}
                    onChange= {item.onChange} 
                    className={item.className} 
                    id={item.id}/>
            </div>
            )}

            <button className='button' onClick={this.getData}>Calculate</button>
        </div>
        <div className='data-container container'>
            <div className='data'>
                <div>
                    <h5>Loan Balance: </h5>
                    <div className='text'>Debt exclude the interest and the principal:</div>
                    <span className='min-pay'>${debtLeft.toFixed(2)}</span>
                    <h5>Minimum Payment Due: </h5>
                    <div className='text'>As you know, the principal should be at least 1% and we add it to your monthly Interest</div>
                    <div className='text'><b>Monthly Interest: ${this.state.monthlyInterest.toFixed(2)}</b></div>
                    <span className='min-pay'>${minimumDue.toFixed(2)}</span>
                    <h5>Amount of Term Remaining:</h5>
                    <div className='text'>This number is relative to your monthly payment</div>
                    <span className='number-left'>{paymentsLeft.toFixed(1)}</span>

                </div>
            </div>
        </div>
    </div> 

    )
}
}

export default Entries;
