import React from 'react';

class Data extends React.Component {
    constructor(props) {
        super(props)

        this.state = {

        }
    }

    render() {
          const {debt, interest, payment} = this.props;
          const monthlyIntPayment = ((interest * debt) / 100) / 12;
          const minPayment = monthlyIntPayment + debt * 0.01;
          const debtLeft = (debt + monthlyIntPayment) - payment;
          const dueLeft = debt / (debt/100);

    return (
        <div className='data-container container'>
            <div className='data'>
                <div>
                    <h5>Debt Due: </h5>
                    <div className='text'>Initial Debt minus payments received</div>
                    <span className='mini-payment'>{debtLeft}</span>
                    <h5>Minimum Payment: </h5>
                    <div className='text'>Principal will be 1% from the last debt</div>
                    <span className='mini-payment'>{minPayment.toFixed(2)}</span>
                    <h5>Payments left: </h5>
                    <div className='text'>Based on minimum payments</div>
                    <span className='payment-left'>{dueLeft.toFixed(1)}</span>
                </div>
            </div>
        </div>

    )
    }

}

export default Data
