import React from 'react';

class Entries extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      debt: 0,    //debtInput
      interest: 0,  //interestInput
      newInterest: 0, // monthlyInterest
      debtDue: 0,   // remainingDebt
      miniDue: 0,  // minPay
      remainingPayment: 0,
    }
  }

  handleChange = (e) => {
    this.setState({[`${e.target.name}`]:e.target.value})
  }


}

export default Entries;
