import React from 'react';




class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: 0,
      debt: 0,
      payments: 0,
    };
  }

  handleInputChange = (event) => {
    this.setState({ inputValue: event.target.value, debt: event.target.value });
  };

  handlePaymentsChange = (event) => {
    this.setState((prevState) => ({
      debt: prevState.debt - event.target.value,
      payments: prevState.payments + event.target.value,
    }));
  };

  calculateDebt = () => {
    const { inputValue, payments } = this.state;
    return inputValue - payments - inputValue * 0.01;
  };

  render() {
    return (
      <div>
        <input
          type="number"
          value={this.state.inputValue}
          onChange={this.handleInputChange}
        />
        <input
          type="number"
          value={this.state.payments}
          onChange={this.handlePaymentsChange}
        />
        {this.calculateDebt() === 0 ? (
          <h1>We've received your payment.</h1>
        ) : (
          <p>Debt: {this.calculateDebt()}</p>
        )}
      </div>
    );
  }
}



export default Form;
