// ./components/Form.jsx
import React, { Component } from 'react';

class Form extends Component {
  state = {
    amount: 0,
    interest: 0,
    years: 0,
    months: 0,
  }

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    const { amount, interest, years, months } = this.state;
    this.props.onSubmit({ amount, interest, years, months });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label htmlFor="amount">Loan Amount:</label>
          <input type="number" id="amount" name="amount" onChange={this.handleChange} />
        </div>
        <div>
          <label htmlFor="interest">Interest Rate:</label>
          <input type="number" id="interest" name="interest" onChange={this.handleChange} />
        </div>
        <div>
          <label htmlFor="years">Loan Years:</label>
          <input type="number" id="years" name="years" onChange={this.handleChange} />
        </div>
        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default Form;

