import React, { Component } from 'react';
import Form from './components/Form';
import Payment from './components/Payment';
import PaymentField from './components/PaymentField';
import NewDebt from './components/NewDebt';


class App extends Component {
  state = {
    payment: 0,
    amount: 0,
  }

  handleSubmit = ({ amount, interest, years }) => {
    const interestRate = interest / 100 / 12;
    const payments = years * 12;
    const x = Math.pow(1 + interestRate, payments);
    const monthly = (amount * x * interestRate) / (x - 1);

    this.setState({ payment: monthly.toFixed(2), amount });
  }

  handleReset = () => {
    this.setState({ payment: 0 });
  }

  handlePaymentMade = amount => {
    this.setState({ amount });
  }

  render() {
    return (
      <div>
        <Form onSubmit={this.handleSubmit} />
        <Payment payment={this.state.payment} />
        <button onClick={this.handleReset}>Clear</button>

        <PaymentField
          amount={this.state.amount}
          interest={this.state.interest}
          onPaymentMade={this.handlePaymentMade}
          />

      </div>
    );
  }
}

export default App;
