import React from 'react';

const NewDebt = ({ amount, interest }) => {
  const interestRate = interest / 100 / 12;
  const payments = 1;
  const x = Math.pow(1 + interestRate, payments);
  const interestPaid = (amount * x * interestRate) / (x - 1) * payments;
  const principalPaid = amount - interestPaid;

  return (
    <div>
      <h2>New Debt Information</h2>
      <div>
        <strong>New Principal:</strong> ${principalPaid.toFixed(2)}
      </div>
      <div>
        <strong>New Interest:</strong> ${interestPaid.toFixed(2)}
      </div>
      <div>
        <strong>Total Debt:</strong> ${amount.toFixed(2)}
      </div>
    </div>
  );
};

export default NewDebt;
