import React, { Component } from 'react';

class PaymentField extends Component {
  state = {
    payment: 0
  }

  handleChange = e => {
    this.setState({ payment: e.target.value });
  }

  handleSubmit = e => {
    e.preventDefault();
    if (this.state.payment < this.props.amount * 0.01) {
      alert('Minimum payment is 1% of the loan amount');
    } else {
      this.props.onPaymentMade(this.props.amount - this.state.payment);
    }
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label htmlFor="payment">Make a payment:</label>
          <input type="text" id="payment" name="payment" onChange={this.handleChange} />
        </div>
        <button type="submit">Submit</button>
      </form>
    );
  }
}

export default PaymentField;
