import React from "react";

class DebtCalculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      debtAmount: 0,
      interestRate: 0,
      error: ''
    };
    this.handleDebtAmountChange = this.handleDebtAmountChange.bind(this);
    this.handleInterestRateChange = this.handleInterestRateChange.bind(this);
  }

  handleDebtAmountChange(event) {
    if (event.target.value > 10000000) {
      this.setState({
        error: "Debt amount should not be more than 10 millions "
      });
    } else {
      this.setState({
        error: "",
        debtAmount: event.target.value
      });
    }
  }

  render() {
    const debtAmountInDollars = this.state.debtAmount.toLocaleString("en-US", {
      style: "currency",
      currency: "USD"
    });
    const interestRateInPercentage = (this.state.interestRate * 100).toFixed(2) + "%";
    return (
      <div>
        <input
          type="number"
          value={this.state.debtAmount}
          onChange={this.handleDebtAmountChange}
        />
        <p>Debt Amount: {debtAmountInDollars}</p>
        <input
          type="number"
          value={this.state.interestRate}
          onChange={this.handleInterestRateChange}
        />
        <p>Interest Rate: {interestRateInPercentage}</p>
        {this.state.error && (
          <p style={{ color: "red" }}>{this.state.error}</p>
        )}
      </div>
    );
  }
}

export default DebtCalculator;
