import React from 'react'
import Entries from './Entries'

class PaymentDue extends React.Component {
 constructor(props) {
    super(props)

    this.component = React.createRef()

    this.state = {
        entry: 0,     //input
        history: [],    //list
        totalPaid: 0,
        miniPay: 0,
        amountLeft: 0,
        totalPaidOff: 0,
        isModal: false,
    }
 }

 handleChange = (e) => {
    this.setState({entry: parseFloat(e.target.value)})
 }

onClick = () => {
    this.computeDebt()

    if (this.state.entry.toFixed(2) === this.state.totalPaidOff) {
        this.popUp()
    }
    else if (this.state.entry > this.state.totalPaidOff) {
        alert(`Your payment should be smaller or equal to the amount owed!!`)
    }
    else if (this.state.entry >= this.state.amountLeft) {
        alert(`You payment should be smaller than the remaining debt, in case you're trying to pay the whole amount, your amount entered should equal : $${this.state.totalPaidOff} `)
    }
    else if (this.state.entry >= this.state.miniPay.toFixed(2)) {
        this.listPayments();
        this.updatePayment();
        this.component.current.computeDebt();
    }
    else {
        alert(`Payments must be equal or greater than ${this.state.miniPay.toFixed(2)}`)
    }
}

listPayments = () => {
    let newItem = {
        label: this.state.entry,
        id: Date.now(),
    }

    this.setState(prevState => ({
        history: [...prevState.history, newItem]
    }))
}

updatePayment = () => {
    this.setState({totalPaid: this.state.totalPaid + this.state.entry})
}

computeDebt = () => {
    let total = this.state.amountLeft + this.state.miniPay;
    this.setState({totalPaidOff: total.toFixed(2)})
}

reset = () => {
    this.setState({
        entry: 0,     //input
        history: [],    //list
        totalPaid: 0,
        miniPay: 0,
        amountLeft: 0,
        totalPaidOff: 0,
        isModal: false,
    });
  this.component.current.reset();
}







}

export default PaymentDue;
