import React from "react";


class Entries extends React.Component {
  constructor(props) {
    super(props)
    this.state={
        initialDebt: 20000,
        initialInterest:5,
        monthlyInterest:0,
        debtLeft:0,
        minimumDue:0,
        paymentsLeft:0,
    }
}

// set states for sliders
handleChange =  (e) => {
    this.setState({[`${e.target.name}`]:e.target.value})
}


captureData = () => {
    // initial data
    const{initialDebt, initialInterest} = this.state
    let debt = initialDebt;
    let monthlyIntPay = ((initialInterest / 100 ) / 12) * debt
    let minTotalPay = monthlyIntPay + (debt * 0.01)
    let totalPayoff = minTotalPay + (debt * 1);

    this.setState({
        monthlyInterest: monthlyIntPay, 
        debtLeft: (debt *1), 
        minimumDue: minTotalPay, 
        paymentsLeft: debt / minTotalPay
    });
    // send min pay to parent
    this.props.getData(minTotalPay, debt, totalPayoff);

}
calcDebt= () => {
if (this.state.debtLeft > 100) {
    const{ initialInterest, debtLeft} = this.state
    const {payment} = this.props;

    let annualInt = initialInterest / 100;
    let monthlyInt = annualInt / 12;
    let monthlyIntPay = monthlyInt * debtLeft
    let newMinPay = monthlyIntPay + (debtLeft * 0.01)
    let principlePaid = payment - monthlyIntPay
    let newDebt= debtLeft - principlePaid
    let totalPayoff = newMinPay + newDebt;

    this.setState({
        monthlyInterest: monthlyInt * newDebt,
        debtLeft: (newDebt * 1), 
        minimumDue: newMinPay, 
        paymentsLeft: newDebt / principlePaid
    });
    // Send min pay to parent
    this.props.getData(newMinPay, newDebt, totalPayoff);
    }
else {
    const{debtLeft} = this.state
    const {payment} = this.props;


    let newDebt= debtLeft - (payment)
    let newMinPay = (debtLeft * 0.01) + newDebt
    let totalPayoff = (debtLeft * 0.01) + newDebt

    this.setState({
        debtLeft: (newDebt * 1), 
        minimumDue: newMinPay, 
        paymentsLeft: newDebt / newMinPay
    });

    this.props.getData(newMinPay, newDebt, totalPayoff);
    }
} 
reset=()=>{
    this.setState({ 
        initialDebt: 20000, 
        initialInterest:5, 
        monthlyInterest:0, 
        debtLeft:0, 
        minimumDue:0, 
        paymentsLeft:0,})
}

render() {

    const {initialDebt, initialInterest, debtLeft, minimumDue, paymentsLeft } = this.state;

    const inputData = [
        {header:'Total Debt', label: `$${initialDebt}`, name:'initialDebt', type:"range",min:"1000", 
        max:"100000", value: initialDebt ,step:'500', onChange: this.handleChange , className:"slider", id:"debtRange"},
        {header:'Total Yearly Interest', label: `${initialInterest}%`, name:'initialInterest', type:"range",min:"1", 
        max:"20", value: initialInterest ,step:'0.1', onChange: this.handleChange , className:"slider", id:"interestRange"}
    ]
    return (

    <div>    
        <div className='container inputs'>
            {inputData.map((item) => 
            <div>
                <h2>{item.header}</h2>
                <div className='total'>{item.label}</div>
                <input 
                    name={item.name}
                    type={item.type} 
                    min={item.min} 
                    max={item.max} 
                    value={item.value} 
                    step={item.step}
                    onChange= {item.onChange} 
                    className={item.className} 
                    id={item.id}/>
            </div>
            )}

            <button className='button' onClick={this.captureData}>Calculate</button>
        </div>
        <div className='data-container container'>
            <div className='data'>
                <div>
                    <h5>Debt Remaining: </h5>
                    <div className='subtext'>Original Debt minus principal paid</div>
                    <span className='min-pay'>${debtLeft.toFixed(2)}</span>

                    <h5>Minimum Payment: </h5>
                    <div className='subtext'>Monthly interest plus 1% payment on principal</div>
                    <div className='subtext'><b>Monthly Interest: ${this.state.monthlyInterest.toFixed(2)}</b></div>
                    <span className='min-pay'>${minimumDue.toFixed(2)}</span>

                    <h5>Number of Payments Left:</h5>
                    <div className='subtext'>Based on last monthly payment</div>
                    <span className='number-left'>{paymentsLeft.toFixed(1)}</span>

                </div>
            </div>
        </div>
    </div> 


    )
}
}

export default Entries;
