import React from "react";
import Entries from "./Entries";

class PaymentDue extends React.Component {
  constructor(props) {
    super(props);

    this.component = React.createRef();

    this.state = {
      input: 0, //input
      history: [], //list
      totalPaid: 0,
      miniPay: 0,
      amountLeft: 0,
      totalPaidOff: 0,
      isModal: false,
    };
  }

  handleChange = (e) => {
    this.setState({ input: parseFloat(e.target.value) })
  };

  onClick = () => {
    this.computeDebt();

    if (this.state.input.toFixed(2) === this.state.totalPaidOff) {
      this.popUp();
    } else if (this.state.input > this.state.totalPaidOff) {
      alert(`Your payment should be smaller or equal to the amount owed!!`);
    } else if (this.state.input >= this.state.amountLeft) {
      alert(
        `You payment should be smaller than the remaining debt, in case you're trying to pay the whole amount, your amount entered should equal : $${this.state.totalPaidOff} `
      );
    } else if (this.state.input >= this.state.miniPay.toFixed(2)) {
      this.listPayments();
      this.updatePayment();
      this.component.current.computeDebt();
    } else {
      alert(
        `Payments must be equal or greater than ${this.state.miniPay.toFixed(
          2
        )}`
      );
    }
  };

  listPayments = () => {
    let newItem = {
      label: this.state.input,
      id: Date.now(),
    };

    this.setState((prevState) => ({
      history: [...prevState.history, newItem],
    }));
  };

  updatePayment = () => {
    this.setState({ totalPaid: this.state.totalPaid + this.state.input });
  };

  computeDebt = () => {
    let total = this.state.amountLeft + this.state.miniPay;
    this.setState({ totalPaidOff: total.toFixed(2) });
  };

  popUp = () => {
    this.setState({ isModal: true });
  };

  reset = () => {
    this.setState({
      input: 0, //input
      history: [], //list
      totalPaid: 0,
      miniPay: 0,
      amountLeft: 0,
      totalPaidOff: 0,
      isModal: false,
    });
    this.component.current.reset();
  };

  render() {
    let { input, history, totalPaid, miniPay, amountLeft, totalPaidOff } =
      this.state;

    const modalVisible = this.state.isModal ? "is-visible" : "";
    return (
      <header className="app-header">
        <div className={`modal ${modalVisible}`}>
          <h2>
            We received you payment, Thank you <br />
            You're Debt Free!
          </h2>
          <button className="button" onClick={this.reset}>
            Try Another Amount
          </button>
        </div>
        <div className="payment container">
          <h3>Payments</h3>
          <input
            type="number"
            step="0.01"
            min={miniPay.toFixed(2)}
            max={amountLeft}
            value={input}
            placeholder="00"
            onChange={this.handleChange}
          />
          <button className="button" onClick={this.onClick}>
            Make Payment
          </button>
          <div className="number">Minimum Due: {miniPay.toFixed(2)}</div>
          <div className="number"> Loan Payoff Amount: {totalPaidOff}</div>
          <h3>Log</h3>
          <ul className="pay-list">
            {history.map((item) => (
              <li key={item.id}>${item.label}</li>
            ))}
          </ul>
          <h5>Total Amount Paid: </h5>
          <span className="amount-left">{totalPaid.toFixed(2)}</span>
        </div>

        <Entries
          ref={this.component}
          payment={input}
          childFun={this.childFunc}
          getData={this.getData}
        />
      </header>
    );
  }
}

export default PaymentDue;
