import React, { Component } from 'react';
import Form from './Form';
import Payment from './Payment';
import PaymentField from './PaymentField';
import NewDebt from './NewDebt';

class App extends Component {
state = {
amount: 0,
interest: 0,
years: 0,
payment: 0
}

handleSubmit = ({ amount, interest, years }) => {
const interestRate = interest / 100 / 12;
const payments = years * 12;
const payment = (amount * interestRate) / (1 - (1 + interestRate) ** (-payments));
this.setState({ amount, interest, years, payment });
}

handlePaymentMade = newAmount => {
this.setState({ amount: newAmount });
}

render() {
return (
<div>
<Form onSubmit={this.handleSubmit} />
<Payment payment={this.state.payment} />
<PaymentField amount={this.state.amount} onPaymentMade={this.handlePaymentMade} />
<NewDebt amount={this.state.amount} interest={this.state.interest} />
</div>
);
}
}

export default App;
