import React, { Component } from 'react';

class NewDebt extends Component {
  state = {
    newDebt: this.props.amount,
  }

  componentDidUpdate(prevProps) {
    if (prevProps.amount !== this.props.amount || prevProps.paymentMade !== this.props.paymentMade) {
      const interestRate = this.props.interest / 100 / 12;
      let newDebt = this.state.newDebt;
      if (this.props.paymentMade >= this.props.payment) {
        newDebt = this.state.newDebt - this.props.paymentMade + this.state.newDebt * interestRate;
      } else {
        newDebt = this.state.newDebt - this.props.payment + this.state.newDebt * interestRate;
      }
      this.setState({ newDebt });
    }
  }

  render() {
    return (
      <div>
        <h2>New Debt: {this.state.paymentMade}</h2>
      </div>
    );
  }
}

export default NewDebt;
