import React from "react";

class Entries extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      debtInput: 0, //debtInput
      interest: 0, //interestInput
      newInterest: 0, // monthlyInterest
      debtDue: 0, // remainingDebt
      miniDue: 0, // minPay
      totalPaid: 0, //remainingPayment
    };
  }

  handleChange = (e) => {
    this.setState({ [`${e.target.name}`]: e.target.value });
  };

  dataFromInput = () => {
    const { debtInput, interest } = this.state;
    let debt = debtInput * 1;
    let monthlyIntPayment = (interest * debt) / 100 / 12;
    let minPayment = monthlyIntPayment + debt * 0.01; //minTotalPay
    let debtFree = minPayment + debt; //totalPayoff

    this.setState({
      newInterest: (interest * debtInput) / 100 / 12,
      debtDue: debt * 1,
      miniDue: minPayment,
      totalPaid: debt / minPayment,
    });

    this.props.dataFromInput(minPayment, debt, debtFree);
  };

  computeDebt = () => {
    if (this.state.debtDue > 100) {
      const { interest, debtDue } = this.state;
      const { payment } = this.props;

      let monthlyInt = interest / 100 / 12;
      let monthlyIntPay = monthlyInt * debtDue;
      let newMiniDue = monthlyIntPay + debtDue * 0.01;
      let principlePaid = payment - monthlyIntPay;
      let newDebt = debtDue - principlePaid;
      let debtFree = newMiniDue + newDebt;

      this.setState({
        newInterest: monthlyInt * debtDue,
        debtDue: newDebt * 1,
        miniDue: newMiniDue,
        totalPaid: newDebt / principlePaid,
      });

      this.props.dataFromInput(newMiniDue, newDebt, debtFree);
    } else {
      const { debtDue } = this.state;
      const { payment } = this.props;

      let newDebt = debtDue - payment;
      let newMiniDue = debtDue * 0.01 + newDebt;
      let totalPaid = debtDue * 0.01 + newDebt;

      this.state({
        debtDue: newDebt,
        miniDue: newMiniDue,
        totalPaid: newDebt / newMiniDue,
      });

      this.props.dataFromInput(newMiniDue, newDebt, totalPaid);
    }
  };

  resetTheCalculator = () => {
    this.setState({
      debtInput: 0, //debtInput
      interest: 0, //interestInput
      newInterest: 0, // monthlyInterest
      debtDue: 0, // remainingDebt
      miniDue: 0, // minPay
      totalPaid: 0,
    });
  };
}

export default Entries;
