import React from 'react'

class TotalDue extends React.Component {
constructor(props) {
  super(props)
}

handleChange = ({target: {name, value}}) => {this.setState({[name]: parseFloat(value)})}

handleSubmit = e => {
  e.preventDefault();
}

handleClick = () => {
  const {debtAmount, interest} = this.props;
  this.setState({
    debtAmount: debtAmount + interest
  })
}




  render() {

    const {debtAmount, interest} = this.props;

    return (
   <div>
    <form>
      <input type="number"
      name='debtAmount'
      autoComplete='off'
      placeholder='$'
      value={debtAmount}
      onChange={this.handleChange} />
      <br />
      <input type="number"
      name='interest'
      autoComplete='off'
      placeholder='%'
      value={interest}
      onChange={this.handleChange} />

      <button onClick={this.handleClick}>Count The Total Due</button>
      </form>
      <DebtCounter debtAmount={debtAmount} interest={interest} />
      </div>
    )
  }
}

export default TotalDue;
