import React from 'react';




const data = {
  debtAmount: 0,
  interest: 0,
  minPrincipal: 0,
  minPayment: 0,
  payment: 0,
  interestPayed: 0,
  principal: 0,
  newBalance: 0,
  historyPayment: [],
};


class Form extends React.Component{
constructor(props) {
  super(props)
  this.state = data
}

handleInputChange = (event) => {
  this.setState({ debtAmount: event.target.value, newBalance: event.target.value });
};

handlePaymentsChange = (event) => {
  this.setState((prevState) => ({
    newBalance: prevState.newBalance - event.target.value,
    payments: prevState.payments + event.target.value,
  }));
};

calculateDebt = () => {
  const { debtAmount, payments } = data;
  return debtAmount - payments - debtAmount * 0.01;
};

render() {
  return (
    <div>
      <input
        type="number"
        value={data.debtAmount}
        onChange={this.handleInputChange}
      />
      <input
        type="number"
        value={data.payments}
        onChange={this.handlePaymentsChange}
      />
      {this.calculateDebt() === 0 ? (
        <h1>We've received your payment.</h1>
      ) : (
        <p>newBalance: {this.calculateDebt()}</p>
      )}
    </div>
  );
}

}

export default Form;
