import React from 'react'

class TotalDue extends React.Component {
constructor(props) {
  super(props)
}

handleChange = ({target: {name, value}}) => {this.setState({[name]: parseFloat(value)})}

handleSubmit = e => {
  e.preventDefault();
}

newDebt = () => {
  const {debt, interest} = this.props
  let paymentInterest = (interest * debt) / 100;
  console.log(paymentInterest)
}




  render() {

    const {debtAmount, interest} = this.props;

    return (
    <form onSubmit={this.handleSubmit}>
      <input type="number"
      name='debtAmount'
      autoComplete='off'
      placeholder='$'
      value={debtAmount}
      onChange={this.handleChange} />
      <br />
      <input type="number"
      name='interest'
      autoComplete='off'
      placeholder='%'
      value={interest}
      onChange={this.handleChange} />

      <button type='submit' onChange={this.newDebt}>Count The Total Due</button>
    </form>
    )
  }
}

export default TotalDue;
