import React from 'react';

const Form = ({ onSubmit }) => {
  const handleSubmit = e => {
    e.preventDefault();
    const amount = parseFloat(e.target.elements.amount.value);
    const interest = parseFloat(e.target.elements.interest.value);
    const years = parseFloat(e.target.elements.years.value);
    onSubmit({ amount, interest, years });
  }

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="amount">Loan amount:</label>
        <input type="text" id="amount" name="amount" />
      </div>
      <div>
        <label htmlFor="interest">Interest rate:</label>
        <input type="text" id="interest" name="interest" />
      </div>
      <div>
        <label htmlFor="years">Loan period (in years):</label>
        <input type="text" id="years" name="years" />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}

export default Form;
