import React from 'react';


class LoanCalculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loanAmount: 0,
      interestRate: 0,
      loanTerm: 0,
      interestAmount: 0,
      principal: 0,
      minimumPayment: 0,
    };
  }

  updateState = (name, value) => {
    this.setState({
      [name]: value
    });
  }

  handleLoanAmountChange = (event) => {
    this.updateState("loanAmount", event.target.value);
  }

  handleInterestRateChange = (event) => {
    this.updateState("interestRate", event.target.value);
  }

  handleLoanTermChange = (event) => {
    this.updateState("loanTerm", event.target.value);
  }

  calculateInterestAmount = () => {
    const { loanAmount, interestRate } = this.state;
    const interestAmount = loanAmount * (interestRate / 100);
    this.setState({ interestAmount });
  }

  calculatePrincipal = () => {
    const { loanAmount, interestAmount } = this.state;
    const principal = loanAmount - interestAmount;
    this.setState({ principal });
  }

  calculateMinimumPayment = () => {
    const { principal, loanTerm } = this.state;
    const minimumPayment = principal / loanTerm;
    this.setState({ minimumPayment });
  }

  render() {
    return (
      <div>
        <form>
          <label>
            Loan Amount:
            <input type="text" value={this.state.loanAmount} onChange={this.handleLoanAmountChange} />
          </label>
          <br />
          <label>
            Interest Rate:
            <input type="text" value={this.state.interestRate} onChange={this.handleInterestRateChange} />
          </label>
          <br />
          <label>
            Loan Term:
            <input type="text" value={this.state.loanTerm} onChange={this.handleLoanTermChange} />
          </label>
        </form>
        <button onClick={this.calculateInterestAmount}>Calculate Interest Amount</button>
        <br />
        <button onClick={this.calculatePrincipal}>Calculate Principal</button>
        <br />
        <button onClick={this.calculateMinimumPayment}>Calculate Minimum Payment</button>
        <br />
        <p>Interest Amount: {this.state.interestAmount}</p>
        <p>Principal: {this.state.principal}</p>
        <p>Minimum Payment: {this.state.minimumPayment}</p>
      </div>
    );
  }
}

export default LoanCalculator;
