import React, { useState } from 'react';

const PaymentField = ({ amount, interest, onPaymentMade }) => {
  const [payment, setPayment] = useState(0);

  const handleChange = event => {
    setPayment(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();

    if (payment < (amount / 100)) {
      alert('Minimum payment is 1% of the total amount');
      return;
    }

    const interestRate = interest / 100 / 12;
    const payments = 1;
    const x = Math.pow(1 + interestRate, payments);
    const interestPaid = (amount * x * interestRate) / (x - 1) * payments;
    const newAmount = amount - payment + interestPaid;

    onPaymentMade(newAmount);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="payment">Make a Payment:</label>
        <input
          type="number"
          id="payment"
          value={payment}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default PaymentField;
