import React from 'react'
import Entries from './Entries'

class PaymentDue extends React.Component {
 constructor(props) {
    super(props)

    this.component = React.createRef()

    this.state = {
        entry: 0,     //input
        history: [],    //list
        totalPaid: 0,
        miniPay: 0,
        amountLeft: 0,
        totalPaidOff: 0,
        isModal: false,
    }
 }

 handleChange = (e) => {
    this.setState({entry: parseFloat(e.target.value)})
 }

onClick = () => {
    this.computeDebt()
}







}

export default PaymentDue;
