import React, { Component } from 'react';

class DebtCalculator extends Component {
  constructor(props) {
    super(props);
    this.state = {
      debtAmount: '',
      interest: ''
    };
  }
  handleDebtAmountChange = (event) => {
    //validate the input to only accept numbers and limit the debt amount to 10 million
    if (!isNaN(event.target.value) && event.target.value <= 10000000) {
      this.setState({ debtAmount: event.target.value });
    }
  }
  handleInterestChange = (event) => {
    //validate the input to only accept numbers and limit the interest to 30%
    if (!isNaN(event.target.value) && event.target.value <= 30) {
      this.setState({ interest: event.target.value });
    }
  }
  render() {
    return (
      <div>
        <label>Debt Amount:</label>
        <input
          type="text"
          value={this.state.debtAmount}
          onChange={this.handleDebtAmountChange}
        />
        <br />
        <label>Interest:</label>
        <input
          type="text"
          value={this.state.interest}
          onChange={this.handleInterestChange}
        />
      </div>
    );
  }
}

export default DebtCalculator;
