import React from 'react';

class Data extends React.Component {
    constructor(props) {
        super(props)

    }

    render() {

    return (
        <div className='data-container container'>
            <div className='data'>
                <div>
                    <h5>Debt Due: </h5>
                    <div className='text'>Initial Debt minus payments received</div>
                    <span className='mini-payment'>{this.props.debtLeft}</span>
                    <h5>Minimum Payment: </h5>
                    <div className='text'>Principal will be 1% from the last debt</div>
                    <span className='mini-payment'>{this.props.minPayment.toFixed(2)}</span>
                    <h5>Payments left: </h5>
                    <div className='text'>Based on minimum payments</div>
                    <span className='payment-left'>{this.props.dueLeft.toFixed(1)}</span>
                </div>
            </div>
        </div>

    )
    }

}

export default Data
