import React, { Component } from 'react';
import Form from './components/Form';
import Payment from './components/Payment';

class App extends Component {
  state = {
    payment: 0,
  }

  handleSubmit = ({ amount, interest, years }) => {
    const interestRate = interest / 100 / 12;
    const payments = years * 12;
    const x = Math.pow(1 + interestRate, payments);
    const monthly = (amount * x * interestRate) / (x - 1);

    this.setState({ payment: monthly.toFixed(2) });
  }

  handleReset = () => {
    this.setState({ payment: 0 });
  }


  render() {
    return (
      <div>
        <Form onSubmit={this.handleSubmit} />
        <Payment payment={this.state.payment} />
        <Form onSubmit={this.handleSubmit} />
        <Payment payment={this.state.payment} />
        <button onClick={this.handleReset}>Reset</button>
      </div>
    );
  }
}

export default App;
