import React from 'react';

class Entries extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      debt: 0,    //debtInput
      interest: 0,  //interestInput
      newInterest: 0, // monthlyInterest
      debtDue: 0,   // remainingDebt
      miniDue: 0,  // minPay
      remainingPayment: 0,
    }
  }

  handleChange = (e) => {
    this.setState({[`${e.target.name}`]:e.target.value})
  }

  dataFromInput = () => {
    const {debt, interest} = this.state
    let newDebt = debt * 1;
    let monthlyIntPayment = ((interest  * newDebt) / 100) / 12;
    let minPayment = monthlyIntPayment + (newDebt * 0.01);
    let debtFree = minPayment + newDebt;

    this.setState({
      newInterest: ((interest  * debt) / 100) / 12
    })
  }


}

export default Entries;
